parameters.parameter:
var items = JSON.parse(value);

if (items.length == 0) {
	return '[]';
}

if (items[0]['{#FW.STATE}'] != 'Installed') {
	return '[]';
}

return JSON.stringify([{"{#SNMPINDEX}": 0, "{#SINGLETON}":""}]);

trigger.expression:
min(/Check Point Next Generation Firewall by SNMP/system.cpu.load.avg1,5m)/last(/Check Point Next Generation Firewall by SNMP/system.cpu.num)>{$LOAD_AVG_PER_CPU.MAX.WARN}
and last(/Check Point Next Generation Firewall by SNMP/system.cpu.load.avg5)>0
and last(/Check Point Next Generation Firewall by SNMP/system.cpu.load.avg15)>0
